const loginForm = (state={email: "", password: ""}, action) => {
  switch (action.type) {
    case "UPDATE_LOGIN_FORM":
      return {
        ...state,
        formData: action.formData
      }
    case "RESET_LOGIN_FORM":
      return state={}
    default:
      return state
  }
}

export default loginForm;




